mapper.select:
SELECT * FROM APP_RESOURCE A WHERE A.RESOURCE_ID IN (SELECT C.RESOURCE_ID FROM APP_USER_ROLE B, APP_ROLE_RESOURCE C, APP_ROLE E WHERE B.ROLE_ID = C.ROLE_ID AND B.ROLE_ID = E.ROLE_ID AND E.VALID_FLAG = '1' AND B.USER_ID = #{userId}) AND NOT EXISTS (SELECT 1 FROM APP_USER_EXCLUDE_RESOURCE D WHERE D.USER_ID = #{userId} AND D.RESOURCE_ID = A.RESOURCE_ID)
AND A.RESOURCE_ID = #{resourceId}
AND A.RESOURCE_TYPE = #{resourceType}
AND A.ACTION_URL = #{actionUrl}
ORDER BY A.PARENT_RESOURCE_ID, A.DISPLAY_ORDER
